// src/RegisterPage.js
import React, { useState } from "react";
import axios from "axios";
import "./RegisterPage.css"; // Optional: for styling
import { Link } from "react-router-dom";

const AddUser = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [address, setAddress] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("Normal"); // Default role
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post("http://localhost:5000/register", {
        role,
        email,
        password,
        name,
        address,
      });
      if (response) {
        setMessage("User added successfully!");
      } else {
        setMessage("Error Registering User use Another Email");
      }
    } catch (error) {
      console.error("Error registering User:", error);
      setMessage("Error Registering User");
    }
  };

  return (
    <div className="register-container">
      <h2>Register</h2>
      <form onSubmit={handleSubmit}>
       
        <div className="form-group">
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="address">Address:</label>
          <input
            type="text"
            id="address"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <button className="register-btn" type="submit">
            Register
          </button>
        </div>
      </form>
      <div className="register-link">
        <p>
          have an account? <Link to="/">Login here</Link>
        </p>
      </div>
      {message && <p className="register-link">{message}</p>}
    </div>
  );
};

export default AddUser;
