import React, { useState, useEffect } from "react";

const ListStore = () => {
    const [allStore, setAllStore] = useState([]);

    // Function to fetch stores from the API
    const fetchStores = async () => {
        try {
            const response = await fetch('http://localhost:5000/stores'); // Make a GET request to /stores endpoint
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json(); // Parse the JSON data from the response
            setAllStore(data); // Update the state with the fetched store data
        } catch (error) {
            console.error('Error fetching stores:', error);
        }
    };

    // Fetch stores when the component mounts
    useEffect(() => {
        fetchStores();
    }, []);

    return (
        <div className="list-Store-container">
            <h1>Product List</h1>
            <div className="liststore-format">
                <p>Image</p>
                <p>Name</p>
                <p>Email</p>
                <p>Address</p>
                <p>Ratings</p>
                {/* <p>Average Rating</p>
                <p>Total Users Submitted Rating</p>
                <p>Remove</p> */}
            </div>
            <div className="listStore-allStore">
                {allStore.length > 0 ? (
                    allStore.map((store) => (
                        <div key={store._id} className="store-item">
                            <img src={store.image} alt="" />
                            <p>{store.name}</p>
                            <p>{store.email}</p>
                            <p>{store.address}</p>
                            <p>{store.tating}</p>
                            {/* <p>{store.totalRatings}</p> */}
                            <button onClick={() => handleRemove(store.email)}>Remove</button>
                        </div>
                    ))
                ) : (
                    <p>No stores available</p>
                )}
                <hr />
            </div>
        </div>
    );
};

// Optional: Function to handle store removal (you can implement this as needed)
const handleRemove = async (email) => {
    try {
        await fetch(`/stores/${email}`, {
            method: 'DELETE'
        });
        // After removing, you might want to refetch the stores or update the state accordingly
    } catch (error) {
        console.error('Error removing store:', error);
    }
};

export default ListStore;
