import React, { useState } from 'react';
import AddStore from './AddStore';
import AddUser from './AddUser';

function Sidebar() {
  const [activeComponent, setActiveComponent] = useState('');

  return (
    <div>
      <nav>
        <button onClick={() => setActiveComponent('addStore')}>Add Store</button>
        <button onClick={() => setActiveComponent('addUser')}>Add User</button>
        {/* More buttons can be added here */}
      </nav>

      {/* Conditional Rendering */}
      {activeComponent === 'addStore' && <AddStore />}
      {activeComponent === 'addUser' && <AddUser />}
      {/* More components can be rendered here based on state */}
    </div>
  );
}

export default Sidebar;
