import React, { useEffect, useState } from 'react';
import axios from 'axios';

const NormalStoreList = () => {
  const [allStore, setAllStore] = useState([]);
  const [filter, setFilter] = useState('');
  const [error, setError] = useState('');
  const [userRatings, setUserRatings] = useState({}); // State for user ratings

  // Function to fetch stores from the API
  const fetchStores = async () => {
    try {
      const response = await fetch("http://localhost:5000/stores"); 
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json(); 
      setAllStore(data); 
      fetchUserRatings(data); // Fetch user ratings after fetching stores
    } catch (error) {
      console.error("Error fetching stores:", error);
      setError('Error fetching stores');
    }
  };

  // Fetch user ratings for each store
  const fetchUserRatings = async (stores) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/user/ratings', {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      const userRatingsData = response.data;
      const ratingsMap = {};
      stores.forEach(store => {
        ratingsMap[store._id] = userRatingsData[store._id] || 'No rating';
      });
      setUserRatings(ratingsMap);
    } catch (error) {
      console.error("Error fetching user ratings:", error);
    }
  };

  // Handle rating submission
  const handleRatingSubmit = async (storeId, rating) => {
    try {
      const token = localStorage.getItem('token');
      await axios.post(`http://localhost:5000/stores/${storeId}/rating`, 
        { rating },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      // Update userRatings state after successful submission
      setUserRatings(prevRatings => ({
        ...prevRatings,
        [storeId]: rating
      }));
    } catch (error) {
      console.error("Error submitting rating:", error);
      setError('Error submitting rating');
    }
  };

  // Filter stores based on the filter input
  const filteredStores = allStore.filter(store =>
    store.name.toLowerCase().includes(filter.toLowerCase()) ||
    store.email.toLowerCase().includes(filter.toLowerCase()) ||
    store.address.toLowerCase().includes(filter.toLowerCase())
  );

  return (
    <div className="store-container">
      {error && <p className="error-message">{error}</p>}
      <input
        type="text"
        placeholder="Filter by name, email, or address"
        value={filter}
        onChange={(e) => setFilter(e.target.value)}
        className="filter-input"
      />
      <table>
        <thead>
          <tr className="store-header">
            <th>Image</th>
            <th>Name</th>
            <th>Email</th>
            <th>Address</th>
            <th>Rating</th>
            <th>Your Rating</th>
            <th>Add/Update Rating</th>
          </tr>
        </thead>
        <tbody>
          {filteredStores.length > 0 ? (
            filteredStores.map((store) => (
              <tr key={store._id} className="store-item">
                <td><img src={store.image} alt={store.name} /></td>
                <td>{store.name}</td>
                <td>{store.email}</td>
                <td>{store.address}</td>
                <td>{store.rating_count > 0 ? (store.rating / store.rating_count).toFixed(1) : "No rating"}</td>
                <td>{userRatings[store._id] !== 'No rating' ? userRatings[store._id] : 'You have not rated this store'}</td>
                <td>
                  <input
                    type="number"
                    min="1"
                    max="5"
                    placeholder="Rate 1-5"
                    onChange={(e) => handleRatingSubmit(store._id, e.target.value)}
                  />
                </td>
                <td>
                  <button onClick={() => handleRemove(store._id)}>Remove</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="7">No stores available</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default NormalStoreList;
