import React from "react";
import "./AddStore.css";
import { useState } from "react";
import axios from "axios";
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';


const schema = yup.object().shape({
  name: yup.string()
    .min(20, 'Name must be at least 20 characters')
    .max(60, 'Name cannot exceed 60 characters')
    .required('Name is required'),
  address: yup.string()
    .max(400, 'Address cannot exceed 400 characters')
    .required('Address is required'),
  password: yup.string()
    .min(8, 'Password must be at least 8 characters')
    .max(16, 'Password cannot exceed 16 characters')
    .matches(/[A-Z]/, 'Password must contain at least one uppercase letter')
    .matches(/[\W_]/, 'Password must contain at least one special character')
    .required('Password is required'),
  email: yup.string()
    .email('Invalid email address')
    .required('Email is required'),
});

const AddStore = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [image,setImage] = useState(null);
  

  const setImgUrl = async (file) => {
    const formData = new FormData();
    formData.append('store', file);

    try {
      const response = await axios.post('http://localhost:5000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.data.success === 1) {
        return response.data.image_url;
        
      } else {
        throw new Error('Image upload failed');
      }
    } catch (error) {
      console.error('Error uploading image:', error);
      setMessage('Error uploading image');
      return null;
    }
  };
  const handleSubmit = async (e) =>{
    e.preventDefault();
    let imageUrl='';
    if (image) {
      imageUrl = await setImgUrl(image);
    }

    try{
      const response = await axios.post('http://localhost:5000/addstore',{
        name,
        email,
        address,
        password,
        image:imageUrl
      })
      if (response) {
        setMessage("Store added successfully!");
      } else {
        setMessage("Email Id Already Registered");
      }
    } catch (error) {
      console.error("Error registering store:", error);
      setMessage("Error Registering Store");
    }
  }

  return (
    <>
      <div className="addstore">
        <form onSubmit={handleSubmit}>
          <div className="group">
            <label> Name:</label>
            <input type="text" id="name" name="name" required placeholder="Enter Name" onChange={(e)=>{setName(e.target.value)}}/>
            {errors.name && <p className="error-message">{errors.name.message}</p>}

          </div>
          <div className="group">
            <label >Email:</label>
            <input type="email" id="email" name="email" required placeholder="Enter Email" onChange={(e)=>{setEmail(e.target.value)}}/>
            {errors.name && <p className="error-message">{errors.name.message}</p>}

          </div>
          <div className="group">
            <label >Password:</label>
            <input type="password" id="password" name="password" required placeholder="Enter Password" onChange={(e)=>{setPassword(e.target.value)}}/>
            {errors.name && <p className="error-message">{errors.name.message}</p>}

          </div>
          <div className="group">
            <label >Address:</label>
            <textarea id="address" name="address" rows="4" required placeholder="Enter Address" onChange={(e)=>{setAddress(e.target.value)}}></textarea>
            {errors.name && <p className="error-message">{errors.name.message}</p>}

          </div>
         
          <div className="group">
            <label >Upload Image:</label>
            <input
              type="file"
              id="image"
              name="image"
              accept="image/*"
              required
              onChange={(e)=>{setImage(e.target.files[0])}}
            />
            <hr />
          </div>
            <div className="group">
            <button type="submit">Submit</button>

            </div>
            {message && <div className="message">{message}</div>}
        </form>
      </div>
    </>
  );
};

export default AddStore;
