import React from "react";
import "./Navbar.css";
// import navlogo from '../../assets/logo.png'
import hamburger_icon from '../Assets/img/hamburger.svg'
import { useState } from "react";
const Navbar = () => {
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);

    const toggleNavbar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    };
  return (
    <>
      <div className="hamburger-menu" onClick={toggleNavbar}>
        <img src={hamburger_icon} alt="Hamburger Menu" />
      </div>
      <div className={isSidebarOpen ? 'sidebar-hidden' : 'navbar'}>
        {/* <img src={navlogo} alt="" className="nav-logo" /> */}
        <p className="nav-logo">Store Rating App</p>
        <div className="searchStore">
          <input type="search" className="search" placeholder="Search Store" />
          <button>Search</button>
        </div>

        <p>Login/Signup</p>
      </div>
    </>
  );
};
export default Navbar;
