import React from "react";
import "./AddStore.css";
import { useState } from "react";
import axios from "axios";
const AddStore = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [image,setImage] = useState('');

  const handleSubmit = async (e) =>{
    e.preventDefault();

    try{
      const response = await axios.post('http://localhost:5000/addstore',{
        name,
        email,
        address,
        password,
        image
      })

    }catch(error){
      console.log(error)
      e.setMessage("Error Registering Store");
    }
  }

  return (
    <>
      <div className="addstore">
        <form onSubmit={handleSubmit}>
          <div className="group">
            <label for="name">Name:</label>
            <input type="text" id="name" name="name" required placeholder="Enter Name" onChange={(e)=>{setName(e.target.value)}}/>
          </div>
          <div className="group">
            <label for="email">Email:</label>
            <input type="email" id="email" name="email" required placeholder="Enter Email" onChange={(e)=>{setEmail(e.target.value)}}/>
          </div>
          <div className="group">
            <label for="address">Password:</label>
            <input type="password" id="password" name="password" required placeholder="Enter Password" onChange={(e)=>{setPassword(e.target.value)}}/>
          </div>
          <div className="group">
            <label for="address">Address:</label>
            <textarea id="address" name="address" rows="4" required placeholder="Enter Address" onChange={(e)=>{setAddress(e.target.value)}}></textarea>
          </div>
         
          <div className="group">
            <label for="image">Upload Image:</label>
            <input
              type="file"
              id="image"
              name="image"
              accept="image/*"
              required
              onChange={(e)=>{setImage(e.target.value)}}
            />
          </div>
            <div className="group">
            <button type="submit">Submit</button>

            </div>
        </form>
      </div>
    </>
  );
};

export default AddStore;
