import React, { useState, useEffect } from "react";
import axios from "axios";
import "./ListUser.css";

const ListUser = () => {
  const [allUser, setAllUser] = useState([]);
  const [filter, setFilter] = useState('');

  // Function to fetch Users from the API
  const fetchUsers = async () => {
    try {
      const response = await fetch("http://localhost:5000/users"); // Make a GET request to /Users endpoint
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json(); // Parse the JSON data from the response
      setAllUser(data); // Update the state with the fetched User data
    } catch (error) {
      console.error("Error fetching Users:", error);
    }
  };

  // Fetch Users when the component mounts
  useEffect(() => {
    fetchUsers();
  }, []);

  

  // Filter Users based on the filter input
  const filteredUsers = allUser.filter(User =>
    User.name.toLowerCase().includes(filter.toLowerCase()) ||
    User.email.toLowerCase().includes(filter.toLowerCase()) ||
    User.address.toLowerCase().includes(filter.toLowerCase())
  );

  return (
    <div className="user-container">
      <input
        type="text"
        placeholder="Filter by name, email, or address"
        value={filter}
        onChange={(e) => setFilter(e.target.value)}
        className="filter-input"
      />
      <table>
        <thead>
          <tr className="user-header">
            <th>Name</th>
            <th>Email</th>
            <th>Address</th>
            <th>Role</th>
          </tr>
        </thead>
        <tbody>
          {filteredUsers.length > 0 ? (
            filteredUsers.map((User) => (
              <tr key={User._id} className="user-item">
                <td>{User.name}</td>
                <td>{User.email}</td>
                <td>{User.address}</td>
                <td>{User.role}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="6">No Users available</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ListUser;
