import React, { useEffect, useState } from 'react';
import axios from "axios";

const NormalStoreList = () => {
  const [allStore, setAllStore] = useState([]);
  const [filter, setFilter] = useState('');
  const [error, setError] = useState(''); // Added state for error messages
  const [userRatings, setUserRatings] = useState({}); // State to store user ratings for each store

  // Function to fetch stores from the API
  const fetchStores = async () => {
    try {
      const response = await fetch("http://localhost:5000/stores"); // Make a GET request to /stores endpoint
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json(); // Parse the JSON data from the response
      setAllStore(data); // Update the state with the fetched store data

      // Fetch user ratings after stores are fetched
      const userRatingsResponse = await fetch("http://localhost:5000/user-ratings", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}` // Send token for authorization
        }
      });
      if (userRatingsResponse.ok) {
        const ratingsData = await userRatingsResponse.json();
        setUserRatings(ratingsData);
      } else {
        throw new Error("Error fetching user ratings");
      }
    } catch (error) {
      console.error("Error fetching stores:", error);
      setError('Error fetching stores'); // Set error message
    }
  };

  // Fetch stores when the component mounts
  useEffect(() => {
    fetchStores();
  }, []);

  const handleRatingSubmit = async (storeId, rating) => {
    try {
      const token = localStorage.getItem('token'); // Ensure you have the token
  
      await axios.post(`http://localhost:5000/stores/${storeId}/rating`, {
        rating
      }, {
        headers: {
          Authorization: `Bearer ${token}`, // Send token for authorization
        },
      });
  
      // Fetch updated user ratings after submitting a rating
      const userRatingsResponse = await fetch("http://localhost:5000/user-ratings", {
        headers: {
          Authorization: `Bearer ${token}` // Send token for authorization
        }
      });
      if (userRatingsResponse.ok) {
        const ratingsData = await userRatingsResponse.json();
        setUserRatings(ratingsData);
      } else {
        throw new Error("Error fetching user ratings");
      }
    } catch (error) {
      console.error("Error submitting rating:", error);
      // Optionally set an error message
    }
  };

  // Filter stores based on the filter input
  const filteredStores = allStore.filter(store =>
    store.name.toLowerCase().includes(filter.toLowerCase()) ||
    store.email.toLowerCase().includes(filter.toLowerCase()) ||
    store.address.toLowerCase().includes(filter.toLowerCase())
  );

  return (
    <div className="store-container">
      {error && <p className="error-message">{error}</p>} {/* Display error message */}
      <input
        type="text"
        placeholder="Filter by name, email, or address"
        value={filter}
        onChange={(e) => setFilter(e.target.value)}
        className="filter-input"
      />
      <table>
        <thead>
          <tr className="store-header">
            <th>Image</th>
            <th>Name</th>
            <th>Email</th>
            <th>Address</th>
            <th>Rating</th>
            <th>Your Rating</th>
            <th>Add Your Rating</th>
          </tr>
        </thead>
        <tbody>
          {filteredStores.length > 0 ? (
            filteredStores.map((store) => (
              <tr key={store._id} className="store-item">
                <td><img src={store.image} alt={store.name} /></td>
                <td>{store.name}</td>
                <td>{store.email}</td>
                <td>{store.address}</td>
                <td>{store.rating > 0 ? store.rating : "No rating"}</td>
                <td>
                  {userRatings[store._id] ? (
                    userRatings[store._id]
                  ) : (
                    "You have not rated this store"
                  )}
                </td>
                <td>
                  <input 
                    type="number" 
                    min="1" 
                    max="5"
                    placeholder="Rate (1-5)"
                    onBlur={(e) => handleRatingSubmit(store._id, Number(e.target.value))}
                  />
                  <button>update/submit Rating</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="7">No stores available</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default NormalStoreList;
