import React, { useState } from "react";
import "./Navbar.css";
import hamburger_icon from "../Assets/img/hamburger.svg";

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleNavbar = () => {
    setIsOpen(!isOpen); // Toggle the state between open and closed
  };

  return (
    <>
      <div className="navbar">
        <div className="hamburger-menu" onClick={toggleNavbar}>
          <img src={hamburger_icon} alt="Hamburger Menu" />
        </div>
        <div className={`navbar-content ${isOpen ? "open" : ""}`}>
          <p className="nav-logo">Store Rating App</p>
          <div className="searchStore">
            <input type="search" className="search" placeholder="Search Store" />
            <button>Search</button>
          </div>
          <p className="login-signup">Login/Signup</p>
        </div>
      </div>
    </>
  );
};

export default Navbar;
