import React from "react";
import { useState } from "react";
import "./Navbar.css";
import AddStore from '../AddStore/AddStore'
import AddUser from "../AddUser/AddUser";
const Navbar = () => {
  const [activeComponent, setActiveComponent] = useState('');
  
  return (
    <>
      <div className="navbar">
        {/* <img src={navlogo} alt="" className="nav-logo" /> */}
        <div onClick={() => setActiveComponent('home')}  className="nav-logo">
          <p>Store Rating App</p>
        </div>
        <div className="links">
        
          <div className="add-store-btn">
            <button onClick={() => setActiveComponent('addStore')} style={{ backgroundColor: activeComponent === 'addStore' ? 'rgb(89, 0, 255)' : 'orange' }}>Add Store</button>
          </div>
          <div className="add-user-btn">
            <button onClick={() => setActiveComponent('addUser')} style={{ backgroundColor: activeComponent === 'addUser' ? 'rgb(89, 0, 255)' : 'orange' }}>Add User</button>
          </div>
          <div className="list-user">
            <button onClick={() => setActiveComponent('listUser')} style={{ backgroundColor: activeComponent === 'listUser' ? 'rgb(89, 0, 255)' : 'orange' }} >User List</button>
          </div>
          <div className="list-store">
            <button onClick={() => setActiveComponent('listStore')} style={{ backgroundColor: activeComponent === 'listStore' ? 'rgb(89, 0, 255)' : 'orange' }}>Store List</button>
          </div>
          <div className="logout-btn">
            <p>Logout</p>
          </div>
        </div>
      </div>

      {/* Conditional Rendering */}
      {activeComponent === 'addStore' && <AddStore />}
      {activeComponent === 'addUser' && <AddUser />}
      {/* More components can be rendered here based on state */}
    </>
  );
};
export default Navbar;
