import React, { useState, useEffect } from "react";
import axios from "axios"; // Consider using axios for consistency
import "./ListUser.css";

const ListUser = () => {
  const [allUsers, setAllUsers] = useState([]);
  const [filter, setFilter] = useState('');

  // Function to fetch Users from the API
  const fetchUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/users", {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}` // Include token if needed
        }
      });
      setAllUsers(response.data); // Update the state with the fetched User data
    } catch (error) {
      console.error("Error fetching Users:", error);
    }
  };

  // Fetch Users when the component mounts
  useEffect(() => {
    fetchUsers();
  }, []);

  // Filter Users based on the filter input
  const filteredUsers = allUsers.filter(user =>
    user.name.toLowerCase().includes(filter.toLowerCase()) ||
    user.email.toLowerCase().includes(filter.toLowerCase()) ||
    user.address.toLowerCase().includes(filter.toLowerCase())
  );

  return (
    <div className="user-container">
      <input
        type="text"
        placeholder="Filter by name, email, or address"
        value={filter}
        onChange={(e) => setFilter(e.target.value)}
        className="filter-input"
      />
      <table>
        <thead>
          <tr className="user-header">
            <th>Name</th>
            <th>Email</th>
            <th>Address</th>
            <th>Role</th>
          </tr>
        </thead>
        <tbody>
          {filteredUsers.length > 0 ? (
            filteredUsers.map((user) => (
              <tr key={user._id} className="user-item">
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>{user.address}</td>
                <td>{user.role}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4">No Users available</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ListUser;
