import React from "react";
import { useState } from "react";
import "./Navbar.css";
import AddStore from '../AddStore/AddStore'
import AddUser from "../AddUser/AddUser";
const Navbar = () => {
  const [activeComponent, setActiveComponent] = useState('');
  const active ={
    backgroundColor: 'green',
    };
    const notActive ={
      backgroundColor:'greenyellow'
    }
  return (
    <>
      <div className="navbar">
        {/* <img src={navlogo} alt="" className="nav-logo" /> */}
        <div className="nav-logo">
          <p>Store Rating App</p>
        </div>
        <div className="links">
        <div className="home">
            <button onClick={() => setActiveComponent('home')} style={activeComponent==='home'?{active}:{}}>Home</button>
          </div>
          <div className="add-store-btn">
            <button onClick={() => setActiveComponent('addStore')} style={activeComponent==='addStore'?{active}:{}}>Add Store</button>
          </div>
          <div className="add-user-btn">
            <button onClick={() => setActiveComponent('addUser')}>Add User</button>
          </div>
          <div className="logout-btn">
            <p>Logout</p>
          </div>
        </div>
      </div>

      {/* Conditional Rendering */}
      {activeComponent === 'addStore' && <AddStore />}
      {activeComponent === 'addUser' && <AddUser />}
      {/* More components can be rendered here based on state */}
    </>
  );
};
export default Navbar;
