import React, { useEffect, useState } from 'react';
import axios from 'axios';

const NormalStoreList = () => {
  const [allStore, setAllStore] = useState([]);
  const [filter, setFilter] = useState('');
  const [error, setError] = useState(''); // Added state for error messages
  const [userRatings, setUserRatings] = useState({}); // State for user ratings

  // Function to fetch stores from the API
  const fetchStores = async () => {
    try {
      const response = await fetch("http://localhost:5000/stores"); // Make a GET request to /stores endpoint
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json(); // Parse the JSON data from the response
      setAllStore(data); // Update the state with the fetched store data
      fetchUserRatings(); // Fetch user ratings after fetching stores
    } catch (error) {
      console.error("Error fetching stores:", error);
      setError('Error fetching stores'); // Set error message
    }
  };

  // Function to fetch user ratings from the API
  const fetchUserRatings = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch("http://localhost:5000/user-ratings", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json();
      setUserRatings(data); // Update the state with the fetched user ratings
    } catch (error) {
      console.error("Error fetching user ratings:", error);
      setError('Error fetching user ratings'); // Set error message
    }
  };

  // Function to handle rating submission or update
  const handleRatingSubmit = async (storeId, rating) => {
    try {
      if (rating < 1 || rating > 5) {
        throw new Error('Rating must be between 1 and 5');
      }

      const token = localStorage.getItem('token');
      await axios.post(`http://localhost:5000/stores/${storeId}/rating`, { rating }, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      // Refresh the store and user ratings after successful submission
      fetchStores();
    } catch (error) {
      console.error("Error submitting rating:", error);
      setError('Error submitting rating'); // Set error message
    }
  };

  // Filter stores based on the filter input
  const filteredStores = allStore.filter(store =>
    store.name.toLowerCase().includes(filter.toLowerCase()) ||
    store.email.toLowerCase().includes(filter.toLowerCase()) ||
    store.address.toLowerCase().includes(filter.toLowerCase())
  );

  return (
    <div className="store-container">
      {error && <p className="error-message">{error}</p>} {/* Display error message */}
      <input
        type="text"
        placeholder="Filter by name, email, or address"
        value={filter}
        onChange={(e) => setFilter(e.target.value)}
        className="filter-input"
      />
      <table>
        <thead>
          <tr className="store-header">
            <th>Image</th>
            <th>Name</th>
            <th>Email</th>
            <th>Address</th>
            <th>Rating</th>
            <th>Your Rating</th>
            <th>Add/Update Your Rating</th>
          </tr>
        </thead>
        <tbody>
          {filteredStores.length > 0 ? (
            filteredStores.map((store) => (
              <tr key={store._id} className="store-item">
                <td><img src={store.image} alt={store.name} /></td>
                <td>{store.name}</td>
                <td>{store.email}</td>
                <td>{store.address}</td>
                <td>{store.rating_count > 0 ? (store.rating / store.rating_count).toFixed(1) : "No rating"}</td>
                <td>
                  {userRatings[store._id] !== undefined
                    ? userRatings[store._id]
                    : 'You have not rated this store'}
                </td>
                <td>
                  <input
                    type="number"
                    min="1"
                    max="5"
                    placeholder="Rate (1-5)"
                    id={`rating-${store._id}`}
                  />
                  <button onClick={() => {
                    const rating = Number(document.getElementById(`rating-${store._id}`).value);
                    handleRatingSubmit(store._id, rating);
                  }}>
                    Update/Submit Rating
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="7">No stores available</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default NormalStoreList;
