import React, { useState } from "react";
// import './Sidebar.css'
import { Link,Route,Routes } from "react-router-dom";
// import product_icon from '../Assets/img/icon/8.svg'
// import list_product_icon from '../Assets/img/icon/6.svg'
// import hamburger_icon from '../Assets/img/icon/hamburger.svg' // Add your hamburger icon path here
// import dashboard_icon from '../Assets/img/icon/dashboard.svg'
import AddStore from "../AddStore/AddStore";

const Sidebar = () => {
    const [activeComponent, setActiveComponent] = useState(''); // Track which component to show

    // Function to handle link clicks
    const handleComponentChange = (component) => {
      setActiveComponent(component);
    };

    return (
       <>
        <div>
        <button onClick={() => handleComponentChange('addStore')}>Add Store</button>
        <button onClick={() => handleComponentChange('addUser')}>Add User</button>
        {/* Add more buttons as needed */}
        </div>
       </>
    );
}

export default Sidebar;
