// src/NormalStoreList.js
import React, { useState, useEffect } from "react";
import axios from "axios";


const NormalStoreList = () => {
  const [allStore, setAllStore] = useState([]);
  const [filter, setFilter] = useState('');
  const [ratings, setRatings] = useState({}); // To store user ratings

  // Function to fetch stores from the API
  const fetchStores = async () => {
    try {
      const response = await fetch("http://localhost:5000/stores"); // Make a GET request to /stores endpoint
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json(); // Parse the JSON data from the response
      setAllStore(data); // Update the state with the fetched store data

      // Initialize ratings with user's existing ratings if any
      const existingRatings = {};
      data.forEach(store => {
        existingRatings[store._id] = store.userRating || 0; // Default to 0 if no rating
      });
      setRatings(existingRatings);
    } catch (error) {
      console.error("Error fetching stores:", error);
    }
  };

  // Fetch stores when the component mounts
  useEffect(() => {
    fetchStores();
  }, []);

  const handleRemove = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/stores/${id}`); // Adjust to match your API endpoint
      setAllStore(allStore.filter((store) => store._id !== id)); // Ensure the ID field is correct
    } catch (error) {
      console.error("Error removing store:", error);
    }
  };

  const handleRatingChange = (storeId, rating) => {
    setRatings(prevRatings => ({
      ...prevRatings,
      [storeId]: rating
    }));
  };

  const handleSubmitRating = async (storeId) => {
    try {
      await axios.post(`http://localhost:5000/api/stores/${storeId}/rating`, {
        rating: ratings[storeId]
      });
      // Optionally update local store list or refetch stores
      fetchStores(); // Refresh the store data to reflect the new rating
    } catch (error) {
      console.error("Error submitting rating:", error);
    }
  };

  // Filter stores based on the filter input
  const filteredStores = allStore.filter(store =>
    store.name.toLowerCase().includes(filter.toLowerCase()) ||
    store.email.toLowerCase().includes(filter.toLowerCase()) ||
    store.address.toLowerCase().includes(filter.toLowerCase())
  );

  return (
    <div className="store-container">
      <input
        type="text"
        placeholder="Filter by name, email, or address"
        value={filter}
        onChange={(e) => setFilter(e.target.value)}
        className="filter-input"
      />
      <table>
        <thead>
          <tr className="store-header">
            <th>Image</th>
            <th>Name</th>
            <th>Email</th>
            <th>Address</th>
            <th>Overall Rating</th>
            <th>Your Rating</th>
            <th>Submit/Update Rating</th>
          </tr>
        </thead>
        <tbody>
          {filteredStores.length > 0 ? (
            filteredStores.map((store) => (
              <tr key={store._id} className="store-item">
                <td><img src={store.image} alt={store.name} /></td>
                <td>{store.name}</td>
                <td>{store.email}</td>
                <td>{store.address}</td>
                <td>{store.rating_count > 0 ? (store.rating / store.rating_count).toFixed(1) : "No rating"}</td>
                <td>
                  <select
                    value={ratings[store._id] || 0}
                    onChange={(e) => handleRatingChange(store._id, parseInt(e.target.value))}
                  >
                    <option value="0">Rate...</option>
                    {[1, 2, 3, 4, 5].map(value => (
                      <option key={value} value={value}>{value}</option>
                    ))}
                  </select>
                </td>
                <td>
                  <button onClick={() => handleSubmitRating(store._id)}>
                    Submit/Update Rating
                  </button>
                  <button onClick={() => handleRemove(store._id)}>Remove</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="7">No stores available</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default NormalStoreList;
