import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom"; // To navigate between pages

const StoreOwner = () => {
  const [averageRating, setAverageRating] = useState(null);
  const [ratingCount, setRatingCount] = useState(0);
  const [usersWhoRated, setUsersWhoRated] = useState([]);
  const navigate = useNavigate(); // To handle navigation

  // Fetch dashboard data when the component mounts
  useEffect(() => {
    const fetchDashboardData = async () => {
      const token = localStorage.getItem("token");
      if (!token) {
        navigate("/login"); // If no token, redirect to login page
        return;
      }

      try {
        const response = await fetch("http://localhost:5000/store/dashboard", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        const data = await response.json();

        if (response.ok) {
          setAverageRating(data.averageRating);
          setRatingCount(data.ratingCount);
          setUsersWhoRated(data.usersWhoRated);
        } else {
          console.error(data.message);
        }
      } catch (error) {
        console.error("Error fetching dashboard data:", error);
      }
    };

    fetchDashboardData();
  }, [navigate]);

  // Handle Logout: clear token and navigate to login page
  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  // Handle Change Password (redirect to change password page)
  const handleChangePassword = () => {
    navigate("/change-password"); // Assumes you have a ChangePassword component or page
  };

  return (
    <>
      <div className="navbar">
        <div className="nav-logo">
          <p>Store Rating App</p>
        </div>
        <div className="links">
          <div className="change-pass-btn">
            <button onClick={handleChangePassword}>Change Password</button>
          </div>

          <div className="logout-btn">
            <button onClick={handleLogout}>Logout</button>
          </div>
        </div>
      </div>

      {/* Dashboard Content */}
      <div className="dashboard">
        <h1>Store Owner Dashboard</h1>

        <div className="average-rating">
          <h2>Average Rating</h2>
          <p>{ratingCount > 0 ? `${averageRating} (${ratingCount} ratings)` : "No ratings yet"}</p>
        </div>

        <div className="user-ratings">
          <h2>Users Who Rated</h2>
          {usersWhoRated.length > 0 ? (
            <ul>
              {usersWhoRated.map((user) => (
                <li key={user._id}>
                  {user.name} ({user.email})
                </li>
              ))}
            </ul>
          ) : (
            <p>No users have rated your store yet.</p>
          )}
        </div>
      </div>
    </>
  );
};

export default StoreOwner;
