import React, { useState } from "react";
// import './Sidebar.css'
import { Link } from "react-router-dom";
// import product_icon from '../Assets/img/icon/8.svg'
// import list_product_icon from '../Assets/img/icon/6.svg'
// import hamburger_icon from '../Assets/img/icon/hamburger.svg' // Add your hamburger icon path here
// import dashboard_icon from '../Assets/img/icon/dashboard.svg'
// import AddStore from "../AddStore/AddStore";

const Sidebar = () => {
    const [activeComponent, setActiveComponent] = useState(''); // State to track active component


    return (
       <>
        <div>
        <Link to="#" onClick={() => setActiveComponent('addStore')}>Add Store</Link>
        <Link to="#" onClick={() => setActiveComponent('addUser')}>Add User</Link>
        </div>
       </>
    );
}

export default Sidebar;
